package StringConversion;

public class IntegerToString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i= 200;
		String OPValue=String.valueOf(i);
		System.out.println(i+200);//400 - adding the two values
		System.out.println(OPValue+200);//200200 - concentate as it string




	}

}
